import React from "react";
import Link from "next/link";
import Head from "next/head";
import fetch from "isomorphic-fetch";

import { Menu, Dropdown, Button } from "antd";
import Icon from "@ant-design/icons";

import Footer from "../components/Footer";
import Item from "../components/Item";

import firebase from "firebase/app";
import "firebase/firestore";
import firebaseConfig from "../config";

var config = firebaseConfig.firebaseConfig;
if (!firebase.apps.length) {
  firebase.initializeApp(config);
}
const db = firebase.firestore();

export default class Index extends React.Component {
  state = {
    data: [],
    search: "",
    length: "",
  };
  static async getInitialProps() {
    const res = await fetch("https://referral-e242d.firebaseio.com/referrals");
    console.log(res);
    const json = await res.json();
    return {
      data: res,
      length: daata.length,
    };
  }

  //   componentWillMount() {
  //     let daata = [];
  //     let Referrals = db.collection("referrals");
  //     Referrals.get()
  //     .then((querySnapshot) => {
  //       querySnapshot.forEach((doc) => {
  //         daata.push(doc.data());
  //       });
  //       this.setState({
  //         data: daata,
  //         length: daata.length,
  //       });
  //     });
  //   }

  loadshop() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "shopping") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadprod() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "productive") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadother() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadpayment() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "payment") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }
  loadtravel() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "Travel") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadall() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        daata.push(doc.data());
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (
          doc
            .data()
            .name.toLowerCase()
            .includes(this.state.search.toLowerCase())
        ) {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
      });
    });
  }

  render() {
    console.log(this.props);
    console.log(this.state);
    const menu = (
      <Menu>
        <Menu.Item>
          <a onClick={this.loadall.bind(this)} rel="noopener noreferrer">
            No Filter
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadshop.bind(this)} rel="noopener noreferrer">
            Shopping
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadprod.bind(this)} rel="noopener noreferrer">
            Productivity
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadtravel.bind(this)} rel="noopener noreferrer">
            Travel
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadpayment.bind(this)} rel="noopener noreferrer">
            Payment
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadother.bind(this)} rel="noopener noreferrer">
            Miscallaneous
          </a>
        </Menu.Item>
      </Menu>
    );

