import Link from "next/link";
import Head from "next/head";

import { Menu, Dropdown, Button } from "antd";
import Icon from "@ant-design/icons";

import Footer from "../components/Footer";
import Item from "../components/Item";

import firebase from "firebase/app";
import "firebase/firestore";
import firebaseConfig from "../config";

var config = firebaseConfig.firebaseConfig;
if (!firebase.apps.length) {
  firebase.initializeApp(config);
}
const db = firebase.firestore();

const Index = () => {
  const state = {
    data: [],
    search: "",
    length: "",
  };

  let daata = [];
  let Referrals = db.collection("referrals");
  Referrals.get().then((querySnapshot) => {
    querySnapshot.forEach((doc) => {
      console.log(doc.data());
      daata.push(doc.data());
    });
    this.setState({
      data: daata,
      length: daata.length,
    });
  });

  const loadshop = () => {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "shopping") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  };

  const loadprod = () => {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "productive") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  };

  const loadother = () => {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  };

  const loadpayment = () => {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "payment") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  };

  const loadtravel = () => {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "Travel") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  };

  const loadall = () => {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        daata.push(doc.data());
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  };

  const onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (
          doc
            .data()
            .name.toLowerCase()
            .includes(this.state.search.toLowerCase())
        ) {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
      });
    });
  };
  const menu = (
    <Menu>
      <Menu.Item>
        <a onClick={loadall} rel="noopener noreferrer">
          No Filter
        </a>
      </Menu.Item>
      <Menu.Item>
        <a onClick={loadshop} rel="noopener noreferrer">
          Shopping
        </a>
      </Menu.Item>
      <Menu.Item>
        <a onClick={loadprod} rel="noopener noreferrer">
          Productivity
        </a>
      </Menu.Item>
      <Menu.Item>
        <a onClick={loadtravel} rel="noopener noreferrer">
          Travel
        </a>
      </Menu.Item>
      <Menu.Item>
        <a onClick={loadpayment} rel="noopener noreferrer">
          Payment
        </a>
      </Menu.Item>
      <Menu.Item>
        <a onClick={loadother.bind(this)} rel="noopener noreferrer">
          Miscallaneous
        </a>
      </Menu.Item>
    </Menu>
  );
  console.log(state);
  let List = "";
  if (state.data == null) {
    List = "Loaing...";
  } else {
    if (state.data.length > 0) {
      List = state.data.map((item) => <Item key={item.name} item={item} />);
    } else {
      {
        List = <h4 className="mt-5">Loading...</h4>;
      }
    }
  }

  return (
    <div>
      <div className="container mt-2 mb-5">
        <Head>
          <meta charset="utf-8" />
          <link
            rel="stylesheet"
            href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css"
            integrity="sha384-9aIt2nRpC12Uk9gS9baDl411NQApFmC26EwAOH8WgZl5MYYxFfc+NcPb1dKGj7Sk"
            crossorigin="anonymous"
          />
          <link rel="icon" href="%PUBLIC_URL%/favicon.ico" />
          <meta name="viewport" content="width=device-width, initial-scale=1" />
          <meta name="theme-color" content="#000000" />
          <meta
            name="description"
            content="Grab Coupon code is coupon codes / referrals for new users. Grab Coupons and Save Money by online offers, deals, discount by our latest Promo codes.  "
          />
          <meta
            name="keyword"
            content="grabon, grab on,grabcouponcode,grab coupon,coupon code,coupon, referral, earn money online, first users, new users, save money,save, Promo code, deals, discount,latest coupon, online shopping "
          />
          <meta
            property="og:description"
            content="Find Latest Coupons &amp; Offers For Online Shopping Sites. GrabOn Will Ensure You Get The Right Promo Codes, Deals &amp; Discount E-Gift Cards For 2020 | SAVE ON EVERYTHING"
          />
          <meta property="og:type" content="Website" />
          <title>Grab Coupon Code</title>
        </Head>
        <div className="row mb-3">
          <div className="col col-12 col-lg-3 col-sm-12 col-md-12 mt-2 mb-2 justify-content-center">
            <Dropdown overlay={menu}>
              <Button style={{ width: "100%", height: "2.7em" }}>
                Categories <Icon type="down" />
              </Button>
            </Dropdown>
          </div>
          <div className="justify-content-center order-first  mt-2 mb-2  align-items-center col col-12 col-lg-5 col-sm-12 col-md-12 ">
            <div className="col justify-content-center  align-items-center">
              <div className="d-flex flex-row">
                <Link href="/">
                  <a>
                    <b
                      style={{
                        fontSize: "210%",
                        fontFamily: "Sriracha, cursive",
                      }}
                    >
                      Run-Coupon
                    </b>

                    <span
                      style={{
                        fontSize: "50%",
                        fontFamily: "Sriracha, cursive",
                        backgroundColor: "white",
                      }}
                      className="rounded-pill border border-danger"
                    >
                      <span className="ml-2 mr-2">
                        {this.state.length} offers
                      </span>
                    </span>
                  </a>
                </Link>
              </div>
              <div
                className="d-flex flex-row ml-2"
                style={{ fontWeight: "bold", fontFamily: "Sriracha, cursive" }}
              >
                for new users only.
              </div>
            </div>
          </div>
          <div className="col col-12 col-lg-4 col-sm-12 col-md-12  mt-2 mb-2  justify-content-end">
            <label id="search" style={{ display: "none" }}>
              Search
            </label>
            <input
              id="search"
              style={{}}
              className="form-control"
              placeholder="Search"
              type="text"
              name="search"
              onChange={this.onChange.bind(this)}
              value={this.state.search}
            />
          </div>
        </div>
        <div className="mb-5" style={{ marginBottom: "500px" }}>
          {" "}
          {List}
        </div>
        {/* <a href="https://github.com/Anant016">@anant016</a> */}.
      </div>
      <Footer />
    </div>
  );
};

export default Index;
