import React from "react";
import Link from "next/link";
import Head from "next/head";
import fetch from "isomorphic-fetch";

import { Menu, Dropdown, Button } from "antd";
import Icon from "@ant-design/icons";

import Footer from "../components/Footer";
import ItemBox from "../components/ItemBox";

import * as firebase from "firebase";
import "firebase/database";
import "firebase/firestore";
import firebaseConfig from "../config";

var config = firebaseConfig.firebaseConfig;
if (!firebase.apps.length) {
  firebase.initializeApp(config);
}

const db = firebase.firestore();
const db1 = firebase.database();

export default class Index extends React.Component {
  state = {
    data: null,
    search: "",
    length: "",
  };
  static async getInitialProps({}) {
    let daata = [];
    var ref = await db1.ref("referrals");
    ref.on(
      "value",
      function (snapshot) {
        daata.push(snapshot.val());
      },
      function (errorObject) {}
    );
    return {
      data: daata,
      length: daata.length,
    };
  }

  loadshop() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "shopping") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadprod() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "productive") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadother() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadpayment() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "payment") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }
  loadtravel() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (doc.data().category == "Travel") {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  loadall() {
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        daata.push(doc.data());
      });
      this.setState({
        data: daata,
        length: daata.length,
      });
    });
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
    let daata = [];
    let Referrals = db.collection("referrals");
    Referrals.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if (
          doc
            .data()
            .name.toLowerCase()
            .includes(this.state.search.toLowerCase())
        ) {
          daata.push(doc.data());
        }
      });
      this.setState({
        data: daata,
      });
    });
  }

  render() {
    let inData = this.props.data[0];
    console.log(inData);
    const menu = (
      <Menu>
        <Menu.Item>
          <a onClick={this.loadall.bind(this)} rel="noopener noreferrer">
            No Filter
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadshop.bind(this)} rel="noopener noreferrer">
            Shopping
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadprod.bind(this)} rel="noopener noreferrer">
            Productivity
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadtravel.bind(this)} rel="noopener noreferrer">
            Travel
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadpayment.bind(this)} rel="noopener noreferrer">
            Payment
          </a>
        </Menu.Item>
        <Menu.Item>
          <a onClick={this.loadother.bind(this)} rel="noopener noreferrer">
            Miscallaneous
          </a>
        </Menu.Item>
      </Menu>
    );

    let List = "";

    if (this.state.data != null) {
      List = this.state.data.map((item) => (
        <ItemBox key={item.name} item={item} />
      ));
    } else if (inData && nData.length > 0) {
